import App, { Container } from "next/app";
import Head from "next/head";
import React from "react";
import { Provider } from "react-redux";
import createStore from "../redux/store";
import withRedux from "next-redux-wrapper";
import Token from "../utils/token";

class MyApp extends App {
  static async getInitialProps({ Component, ctx }) {
    let pageProps;
    // Ensure getInitialProps gets called on our child pages
    const locale = Token.getStoredLocale(ctx.req); // Retrieve the locale from cookie or headers
    await ctx.store.dispatch(AppActions.startup({ locale })); // Post startup action with token and locale
    if (Component.getInitialProps) {
      pageProps = await Component.getInitialProps({ ctx });
    }

    return { pageProps };
  }

  render() {
    const { Component, pageProps, store } = this.props;
    if (!initialRender) {
      initialRender = true;
      const locale = store.getState().locale;
      if (locale) {
        Strings.setLanguage(locale);
      }
    }
    return (
      <Container>
        <Provider store={store}>
          <>
            <Head>
              {/*...script and meta tags*/}
              <title>TheProject</title>
            </Head>
            <Header />
            <Component {...pageProps} />
          </>
        </Provider>
      </Container>
    );
  }
}

export default withRedux(createStore)(MyApp);
